  import SideMenu from './components/side-menu/side-menu'
  import HeaderBar from './components/header-bar'
  import TagsNav from './components/tags-nav'
  import User from './components/user'
  import ABackTop from './components/a-back-top'
  import Fullscreen from './components/fullscreen'
  import Language from './components/language'
  import ErrorStore from './components/error-store'
  import siderTrigger from './components/header-bar/sider-trigger'
  import customBreadCrumb from './components/header-bar/custom-bread-crumb'
  import {mapMutations, mapActions, mapGetters} from 'vuex'
  import {getNewTagList, getNextRoute, routeEqual} from '@/libs/util'
  import minLogo from '@/assets/images/logo-min.png'
  import maxLogo from '@/assets/images/logo.png'
  import './main.less'
  import Layout from "../../../node_modules/iview/src/components/layout/layout";
  export default {
    name: 'Main',
    components: {
      Layout, 
			SideMenu,
      HeaderBar,
      Language,
      TagsNav,
      Fullscreen,
      ErrorStore,
      User,
      ABackTop,
      customBreadCrumb,
      siderTrigger
    },
    data () {
      return {
        value1: false,
        collapsed: false,
        layout:'',
        minLogo,
        maxLogo,
        isFullscreen: false,
        screenWidth: 0,
        userInfo: JSON.parse(window.localStorage.getItem('userInfo')),
      }
    },
    computed: {
      ...mapGetters([
        'errorCount'
      ]),
      breadCrumbList () {
        return this.$store.state.app.breadCrumbList
      },
      headerLogoClass () {
        return [
          'main-layout-header-logo',
          this.collapsed ? 'main-layout-header-logo-collapsed' : ''
        ]
      },
      mainClass () {
        return [
          'main-layout-content',
          this.collapsed ? 'main-layout-content-collapsed' : ''
        ]
      },
      tagNavList () {
        return this.$store.state.app.tagNavList
      },
      tagRouter () {
        return this.$store.state.app.tagRouter
      },
      userAvatar () {
        return this.$store.state.user.avatarImgPath
      },
      userName () {
        return this.userInfo.username //'我是吉米小绵羊啊'
      },
      menuList () {
        return this.$store.getters.menuList
      },
      local () {
        return this.$store.state.app.local
      },
      hasReadErrorPage () {
        return this.$store.state.app.hasReadErrorPage
      }
    },
    methods: {
      ...mapMutations([
        'setBreadCrumb',
        'setTagNavList',
        'addTag',
        'setLocal'
      ]),
      ...mapActions([
        'handleLogin'
      ]),
      turnToPage (route) {
        let {name, params, query} = {}
        if (typeof route === 'string') name = route
        else {
          name = route.name
          params = route.params
          query = route.query
        }
        if (name.indexOf('isTurnByHref_') > -1) {
          window.open(name.split('_')[1])
          return
        }
        this.$router.push({
          name,
          params,
          query
        })
      },
      changeLayout(layout){
        if(!layout){
          layout = 'left'
        }
        localStorage.setItem("layout",layout);
        this.$Message.info("选择布局:"+layout)
        this.layout = layout
      },
      handleCollapsedChange (state) {
        this.collapsed = state
      },
      handleCloseTag (res, type, route) {
        if (type === 'all') {
          this.turnToPage(this.$config.homeName)
        } else if (routeEqual(this.$route, route)) {
          if (type !== 'others') {
            const nextRoute = getNextRoute(this.tagNavList, route)
            this.$router.push(nextRoute)
          }
        }
        this.setTagNavList(res)
      },
      handleClick (item) {
        this.turnToPage(item)
      },
      setLayout(){
        let layout = localStorage.getItem("layout");
        if(!layout){
          layout = 'left'
          localStorage.setItem("layout",layout);
        }
        this.layout = layout
      }
    },
    watch: {
      screenWidth(val) {
        const  that = this;
        if (val < 768) {
          // 小于768强制折叠
          that.handleCollapsedChange(true)
        } else {
          that.handleCollapsedChange(false)
        }
      },
      '$route' (newRoute) {
        const {name, query, params, meta} = newRoute
        this.addTag({
          route: {name, query, params, meta},
          type: 'push'
        })
        this.setBreadCrumb(newRoute)
        this.setTagNavList(getNewTagList(this.tagNavList, newRoute))
        //this.$refs['sideMenu'].updateOpenName(newRoute.name)
      }
    },
    mounted () {
      const that = this
      // 宽度适应
      that.screenWidth = document.body.clientWidth
      window.onresize = () => {
        return (() => {
          that.screenWidth = document.body.clientWidth
        })()
      }
      // 设置布局
      this.setLayout()
      // 初始化设置面包屑导航和标签导航
      this.setTagNavList()
      this.addTag({
        route: this.$store.state.app.homeRoute
      })
      this.setBreadCrumb(this.$route)
      // 设置初始语言
      //this.setLocal(this.$i18n.locale)
      // 如果当前打开页面不在标签栏中，跳到homeName页
      if (!this.tagNavList.find(item => item.name === this.$route.name)) {
        this.$router.push({
          name: this.$config.homeName
        })
      }
    }
  }
