import {readUserAgent} from '@/libs/util'

export default {
  name: 'GatewayAccessLog',
  data () {
    return {
      drawer: false,
      currentRow: {},
      loading: false,
      pageInfo: {
        total: 0,
        page: 1,
        limit: 10,
        path: '',
        ip: '',
        serviceId: ''
      },
      columns: [
        {
          type: 'selection',
          width: 60,
          align: 'center'
        },
        {
          title: '请求地址',
          key: 'path',
          width: 220
        },
        {
          title: '请求方式',
          key: 'method',
          width: 100,
          filters: [
            {
              label: 'POST',
              value: 0
            },
            {
              label: 'GET',
              value: 1
            },
            {
              label: 'DELETE',
              value: 2
            },
            {
              label: 'OPTIONS',
              value: 3
            },
            {
              label: 'PATCH',
              value: 4
            }
          ],
          filterMultiple: false,
          filterMethod (value, row) {
            if (value === 0) {
              return row.method === 'POST'
            } else if (value === 1) {
              return row.method === 'GET'
            } else if (value === 2) {
              return row.method === 'DELETE'
            } else if (value === 3) {
              return row.method === 'OPTIONS'
            } else if (value === 4) {
              return row.method === 'PATCH'
            }
          }
        },
        {
          title: 'IP',
          key: 'ip',
          width: 150
        },
        {
          title: '区域',
          key: 'region',
          width: 200
        },
        {
          title: '终端',
          width: 100,
          render: (h, params) => {
            return h('div', readUserAgent(params.row.userAgent).terminal)
          }
        },
        {
          title: '浏览器',
          width: 100,
          render: (h, params) => {
            return h('div', readUserAgent(params.row.userAgent).browser)
          }
        },
        {
          title: '服务名',
          key: 'serviceId',
          width: 200
        },
        {
          title: '响应状态',
          key: 'httpStatus',
          slot: 'httpStatus',
          width: 100
        },
        {
          title: '耗时',
          key: 'useTime',
          render: (h, params) => {
            return h('div', (params.row.useTime ? params.row.useTime : 0) + ' ms')
          },
          width: 100
        },
        {
          title: '请求时间',
          key: 'requestTime',
          width: 200
        },
        {
          title: '详情',
          slot: 'detail',
          fixed: 'right',
          width: 120
        }
      ],
      data: []
    }
  },
  methods: {
    openDrawer (data) {
      this.currentRow = data
      this.drawer = true
    },
    handleSearch (page) {
      if (page) {
        this.pageInfo.page = page
      }
      this.loading = true
      getAccessLogs(this.pageInfo).then(res => {
        this.data = res.data.records
        this.pageInfo.total = parseInt(res.data.total)
      }).finally(() => {
        this.loading = false
      })
    },
    handleResetForm (form) {
      this.$refs[form].resetFields()
    },
    handlePage (current) {
      this.pageInfo.page = current
      this.handleSearch()
    },
    handlePageSize (size) {
      this.pageInfo.limit = size
      this.handleSearch()
    }
  },
  mounted: function () {
    this.handleSearch()
  }
}
